// Variables
var arr         = new Array(),
    inputYear   = "2015-2016",
    checkedOpt  = $("#licenses option:selected").index() + 1,
    storeValues = function() {
      // Remember License
      if ( localStorage.getItem("chosenLicense")) {
        $("#licenses").val(localStorage.getItem("chosenLicense"));
      }
      // Remember Name
      if ( localStorage.getItem("mainName")) {
        $(".inputs[type=text]").val(localStorage.getItem("mainName"));
      }
      $(".inputs[type=text]").on("keyup change", function() {
        localStorage.setItem("mainName", this.value);
      });
      // Remember Email
      if ( localStorage.getItem("mainEmail")) {
        $(".inputs[type=email]").val(localStorage.getItem("mainEmail"));
      }
      $(".inputs[type=email]").on("keyup change", function() {
        localStorage.setItem("mainEmail", this.value);
      });
    };

// Initialize localStorage
storeValues();

// Add license buttons
// Make licenses an array
$("#licenses option").each(function() {
  $(".license-scroll").append("<button class=\"license\">"+ this.value +"</button>");
  return arr.push(this.value);
});

