$(".hold-buttons button").not(".fa-search").click(function() {
  $(document.body).append('<div class="fixedfill preloader hide"></div>');
  $(".preloader").html('<div class="table"><div class="cell"><h1>Downloading libraries!</h1><div class="spinner"><div class="bounce1"></div><div class="bounce2"></div><div class="bounce3"></div></div></div></div>').removeClass("hide");
});

// Download license
$("[data-download=license]").click(function() {
  var activeCode = $(".canvas").val();
  
  var blob = new Blob([ activeCode ], {type: "plain text"});
  saveAs(blob, "LICENSE");
  $(".preloader").remove();
});
// Download all libraries in an archive
$("[data-download=licenses]").click(function() {
  JSZipUtils.getBinaryContent("all-licenses.zip", function(err, data) {
    if(err) {
      throw err; // or handle err
    }

    var zip = new JSZip(data);
    
    var content = zip.generate({type:"blob"});
    saveAs(content, "all-licenses.zip");
    $(".preloader").remove();
    return false;
  });
});

// Mousewhell for designer's horizontal scrollbar
(function() {
  function scrollMenu(e) {
    e = window.event || e;
    var delta = Math.max(-1, Math.min(1, (e.wheelDelta || -e.detail)));
    document.getElementById('licensescroll').scrollLeft -= (delta*40); // Multiplied by 40
    return false;
  }
  if (document.getElementById('licensescroll').addEventListener) {
    // IE9, Chrome, Safari, Opera
    document.getElementById('licensescroll').addEventListener('mousewheel', scrollMenu, false);
    // Firefox
    document.getElementById('licensescroll').addEventListener('DOMMouseScroll', scrollMenu, false);
  } else {
    // IE 6/7/8
    document.getElementById('licensescroll').attachEvent('onmousewheel', scrollMenu);
  }
})();